select aaa.cum030id, cum3.shopmallname as charTitle, sum(IF (aaa.monthseq = '1',aaa.amount,0)) as chartValues1, sum(IF (aaa.monthseq = '2',aaa.amount,0)) as chartValues2, sum(IF (aaa.monthseq = '3',aaa.amount,0)) as chartValues3, sum(IF (aaa.monthseq = '4',aaa.amount,0)) as chartValues4, sum(IF (aaa.monthseq = '5',aaa.amount,0)) as chartValues5, sum(IF (aaa.monthseq = '6',aaa.amount,0)) as chartValues6, sum(IF (aaa.monthseq = '7',aaa.amount,0)) as chartValues7, sum(IF (aaa.monthseq = '8',aaa.amount,0)) as chartValues8, sum(IF (aaa.monthseq = '9',aaa.amount,0)) as chartValues9, sum(IF (aaa.monthseq = '10',aaa.amount,0)) as chartValues10, sum(IF (aaa.monthseq = '11',aaa.amount,0)) as chartValues11, sum(IF (aaa.monthseq = '12',aaa.amount,0)) as chartValues12, sum(IF (aaa.monthseq = '13',aaa.amount,0)) as chartValues13, sum(IF (aaa.monthseq = '14',aaa.amount,0)) as chartValues14 from ( select sub1.monthseq, sub1.cum030id, sum(sub1.amount) as amount from ( select CASE WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -13 DAY)) THEN 1 WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -12 DAY)) THEN 2 WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -11 DAY)) THEN 3 WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -10 DAY)) THEN 4 WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -9 DAY)) THEN 5 WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -8 DAY)) THEN 6 WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -7 DAY)) THEN 7 WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -6 DAY)) THEN 8 WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -5 DAY)) THEN 9 WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -4 DAY)) THEN 10 WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -3 DAY)) THEN 11 WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -2 DAY)) THEN 12 WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -1 DAY)) THEN 13 WHEN dayofmonth(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = dayofmonth(DATE_ADD(concat(#search_day#,'000000'), INTERVAL 0 DAY)) THEN 14 ELSE 0 END AS monthseq, a.cum030id, IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0) as amount from ismodm010 a where DATE_FORMAT(DATE_ADD(a.orderdate, INTERVAL 0 DAY),'%Y-%m-%d') between DATE_FORMAT(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -13 DAY),'%Y-%m-%d') and DATE_FORMAT(DATE_ADD(concat(#search_day#,'000000'), INTERVAL 0 DAY),'%Y-%m-%d') ) sub1 group by sub1.monthseq, sub1.cum030id ) aaa, ( select a.cum030id, sum(IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0)) from ismodm010 a where DATE_FORMAT(DATE_ADD(a.orderdate, INTERVAL 0 DAY),'%Y-%m-%d') between DATE_FORMAT(DATE_ADD(concat(#search_day#,'000000'), INTERVAL -13 DAY),'%Y-%m-%d') and DATE_FORMAT(DATE_ADD(concat(#search_day#,'000000'), INTERVAL 0 DAY),'%Y-%m-%d')
and a.cum030id <> ''
group by a.cum030id order by sum(IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0)) desc limit 10 ) bbb, ismcum030 cum3 where aaa.cum030id = bbb.cum030id and aaa.cum030id = cum3.cum030id group by aaa.cum030id, cum3.shopmallname;

