and prd.byc010id <> ''
group by prd.byc010id order by sum(IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0)) desc limit 10 ) bbb, ismbyc010 byc where aaa.byc010id = bbb.byc010id and aaa.byc010id = byc.byc010id group by aaa.byc010id, byc.bycname;

select aaa.orderitemcode, prd.itemname as charTitle, sum(IF (aaa.monthseq = '1',aaa.amount,0)) as chartValues1, sum(IF (aaa.monthseq = '2',aaa.amount,0)) as chartValues2, sum(IF (aaa.monthseq = '3',aaa.amount,0)) as chartValues3 from ( select sub1.monthseq, sub1.orderitemcode, sum(sub1.amount) as amount from ( select CASE WHEN MONTH(DATE_ADD(a.orderdate, INTERVAL 0 MONTH)) = MONTH(DATE_ADD(concat(#search_day#,'01000000'), INTERVAL -2 MONTH)) THEN 1 WHEN MONTH(DATE_ADD(a.orderdate, INTERVAL 0 MONTH)) = MONTH(DATE_ADD(concat(#search_day#,'01000000'), INTERVAL -1 MONTH)) THEN 2 WHEN MONTH(DATE_ADD(a.orderdate, INTERVAL 0 MONTH)) = MONTH(DATE_ADD(concat(#search_day#,'01000000'), INTERVAL 0 MONTH)) THEN 3 ELSE 0 END AS monthseq, a.orderitemcode, IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0) as amount from ismodm010 a where DATE_FORMAT(DATE_ADD(a.orderdate, INTERVAL 0 DAY),'%Y-%m-%d') between DATE_FORMAT(DATE_ADD(concat(#search_day#,'01000000'), INTERVAL -2 MONTH),'%Y-%m-%d') and DATE_FORMAT(DATE_ADD(LAST_DAY(concat(#search_day#,'01')), INTERVAL 0 MONTH),'%Y-%m-%d') ) sub1 group by sub1.monthseq, sub1.orderitemcode ) aaa, ( select a.orderitemcode, sum(IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0)) from ismodm010 a where DATE_FORMAT(DATE_ADD(a.orderdate, INTERVAL 0 DAY),'%Y-%m-%d') between DATE_FORMAT(DATE_ADD(concat(#search_day#,'01000000'), INTERVAL -2 MONTH),'%Y-%m-%d') and DATE_FORMAT(DATE_ADD(LAST_DAY(concat(#search_day#,'01')), INTERVAL 0 MONTH),'%Y-%m-%d')
and a.orderitemcode <> ''
group by a.orderitemcode order by sum(IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0)) desc limit 10 ) bbb, ismprd010 prd where aaa.orderitemcode = bbb.orderitemcode and aaa.orderitemcode = prd.itemcode group by aaa.orderitemcode, prd.itemname;

