and prd.byc010id <> ''
group by prd.byc010id order by sum(IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0)) desc limit 10 ) bbb, ismbyc010 byc where aaa.byc010id = bbb.byc010id and aaa.byc010id = byc.byc010id group by aaa.byc010id, byc.bycname;

select a.orderitemcode, IFNULL(prd.itemname,a.orderitemcode) as charTitle, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '1',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues1, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '2',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues2, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '3',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues3, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '4',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues4, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '5',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues5, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '6',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues6, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '7',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues7, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '8',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues8, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '9',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues9, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '10',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues10, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '11',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues11, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '12',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues12 from ismodm010 a join ismprd010 prd on a.orderitemcode = prd.itemcode, ( select suba.orderitemcode, sum(IFNULL(suba.orderitemqty,0) * IFNULL(suba.orderitemprice,0)) FROM ismodm010 suba where DATE_FORMAT(DATE_ADD(suba.orderdate, INTERVAL 0 DAY),'%Y-%m-%d') between DATE_ADD(concat(#search_day#,'0101'), INTERVAL 0 DAY) and DATE_ADD(concat(#search_day#,'1231'), INTERVAL 0 DAY)
and IFNULL(suba.orderitemcode,'') <> ''
group by suba.orderitemcode order by sum(IFNULL(suba.orderitemqty,0) * IFNULL(suba.orderitemprice,0)) desc limit 10 ) b where a.orderitemcode = b.orderitemcode and DATE_FORMAT(DATE_ADD(a.orderdate, INTERVAL 0 DAY),'%Y-%m-%d') between DATE_ADD(concat(#search_day#,'0101'), INTERVAL 0 DAY) and DATE_ADD(concat(#search_day#,'1231'), INTERVAL 0 DAY) group by a.orderitemcode, prd.itemname;

select prd.byc010id, byc.bycname as charTitle, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '1',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues1, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '2',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues2, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '3',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues3, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '4',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues4, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '5',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues5, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '6',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues6, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '7',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues7, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '8',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues8, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '9',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues9, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '10',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues10, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '11',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues11, sum(IF (MONTH(DATE_ADD(a.orderdate, INTERVAL 0 DAY)) = '12',IFNULL(a.orderitemqty,0) * IFNULL(a.orderitemprice,0),0)) as chartValues12 from ismodm010 a left outer join ismprd010 prd on a.orderitemcode = prd.itemcode left outer join ismbyc010 byc on byc.byc010id = prd.byc010id, ( select subprd.byc010id, sum(IFNULL(suba.orderitemqty,0) * IFNULL(suba.orderitemprice,0)) FROM ismodm010 suba left outer join ismprd010 subprd on suba.orderitemcode = subprd.itemcode where DATE_FORMAT(DATE_ADD(suba.orderdate, INTERVAL 0 DAY),'%Y-%m-%d') between DATE_ADD(concat(#search_day#,'0101'), INTERVAL 0 DAY) and DATE_ADD(concat(#search_day#,'1231'), INTERVAL 0 DAY)
and subprd.byc010id <> ''
group by subprd.byc010id order by sum(IFNULL(suba.orderitemqty,0) * IFNULL(suba.orderitemprice,0)) desc limit 10 ) b where prd.byc010id = b.byc010id and DATE_FORMAT(DATE_ADD(a.orderdate, INTERVAL 0 DAY),'%Y-%m-%d') between DATE_ADD(concat(#search_day#,'0101'), INTERVAL 0 DAY) and DATE_ADD(concat(#search_day#,'1231'), INTERVAL 0 DAY) group by prd.byc010id, byc.bycname;

select count(*) from ismodm010
 where processdate between concat(DATE_FORMAT(now(),'%Y-%m-%d'),' 0') and concat(DATE_FORMAT(now(),'%Y-%m-%d'),' 9')
   and status = 2;

select count(*) from ismodm010
 where processdate between concat(DATE_FORMAT(DATE_ADD(now(), interval -7 DAY),'%Y-%m-%d'),' 0') and concat(DATE_FORMAT(now(),'%Y-%m-%d'),' 9')
   and status = 2;

select count(*) from ismodm010
 where processdate between concat(DATE_FORMAT(DATE_ADD(now(), interval -30 DAY),'%Y-%m-%d'),' 0') and concat(DATE_FORMAT(now(),'%Y-%m-%d'),' 9')
   and status = 2;
