sqlMap.insert:
    /*ord010DAO.insertManualDetailData*/
	insert into ismodo010 (cum030id, additem, isassign, orderfield, additemopt1, additemopt2 ) 
	values (#cum030id#, #additem#, #isassign#, #orderfield#, #additemopt1#, #additemopt2#)

sqlMap.select:
/*ord020DAO.selectCompList*/ select a.odm010id, a.orderno, DATE_FORMAT(DATE_ADD(a.orderdate, INTERVAL 0 DAY) , '%Y-%m-%d') as orderdate, a.cum010id, a.cum030id, a.byc010id, a.orderuser, a.orderusercontact, a.rcvuser, a.rcvusercontact, a.orderitemid, a.orderitemopt, a.orderitemqty, a.orderitembyprice, a.postno, a.address, a.dlvmemo, a.dlvbyprice, a.dlvno, a.dlvco, a.dlvstatus, a.processdate, a.accountcum, a.accountbuy, a.status, a.cstype, a.uploadfilename, a.uploadviewkey, a.claimstatus, a.claimreason, a.retstatus, a.retqty, a.retprice, IFNULL(IF(IFNULL(a.orderitemname,'') = '', d.itemname, a.orderitemname),'') as orderitemname, IFNULL(IF(IFNULL(a.orderitemprice,'') = '', d.itemprice, a.orderitemprice),'') as orderitemprice, IFNULL(IF(IFNULL(a.dlvprice,'') = '', d.itemdlvprice, a.dlvprice),'') as dlvprice, a.rcvusercontacthp, a.rcvuseremail, b.coname, c.code_nm, IFNULL(d.itemname,'') as itemname, IFNULL(d.itemprice,'0') as itemprice, IFNULL(d.itembuyprice,'0') as itembuyprice, IFNULL(d.itemdlvprice,'0') as itemdlvprice, IFNULL(d.itembuydlvprice,'0') as itembuydlvprice, IFNULL(e.bycname,'') as bycname, f.CODE_NM as ststusNm, g.shopmallname, h.code_nm as cotype2nm, i.code_nm as cotype3nm, IFNULL(j.code_nm,'') as claimstatusnm, IFNULL(k.code_nm,'') as claimreasonnm, IFNULL(l.code_nm,'') as retstatusnm, IFNULL(d.orderitemid,'NOITEM') as chkprod from ismodm010 a left outer join (select t1.*, t2.cumprodcode, t2.cum030id from ismprd010 t1 inner join ismcum040 t2 on t1.itemcode = t2.orderitemid) d ON a.orderitemid = d.cumprodcode and a.cum030id = d.cum030id left outer join ismbyc010 e on d.byc010id = e.byc010id left outer join LETTCCMMNDETAILCODE f on a.status = f.CODE and f.CODE_ID = 'ISM050'
left outer join LETTCCMMNDETAILCODE j on a.claimstatus = j.CODE and j.CODE_ID = 'ISM060' left outer join LETTCCMMNDETAILCODE k on a.claimreason = k.CODE and k.CODE_ID = 'ISM070' left outer join LETTCCMMNDETAILCODE l on a.retstatus = l.CODE and l.CODE_ID = 'ISM080' inner join (SELECT orderno, orderdate, cum010id, cum030id, byc010id, orderuser, orderusercontact, rcvuser, rcvusercontact, orderitemid, orderitemopt, orderitemqty, orderitembyprice, orderitemprice, postno, address, orderitemname FROM ismodm010 where 1=1
odm010id = #odm010id#
) x on a.orderno = x.orderno and a.orderdate = x.orderdate and a.cum010id = x.cum010id and a.cum030id = x.cum030id and a.byc010id = x.byc010id and a.orderuser = x.orderuser and a.orderusercontact = x.orderusercontact and a.rcvuser = x.rcvuser and a.rcvusercontact = x.rcvusercontact and a.orderitemid = x.orderitemid and a.orderitemopt = x.orderitemopt and a.orderitemqty = x.orderitemqty and a.orderitembyprice = x.orderitembyprice and a.orderitemprice = x.orderitemprice and a.postno = x.postno and a.address = x.address and a.orderitemname = x.orderitemname, ismcum010 b left outer join LETTCCMMNDETAILCODE h on b.cotype2 = h.CODE and h.CODE_ID in ('ISM031','ISM032') left outer join LETTCCMMNDETAILCODE i on b.cotype3 = i.CODE and i.CODE_ID in ('ISM041','ISM042','ISM043','ISM044'), LETTCCMMNDETAILCODE c, ismcum030 g where a.cum010id = b.cum010id and a.cum030id = g.cum030id and b.cotype1 = c.CODE and c.CODE_ID = 'ISM020' and a.odm010id = #odm010id# order by b.coname, a.orderno, a.orderitemid limit 1 OFFSET 0
